package Lecture1;

import java.util.Scanner;

public class pattern1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner scn = new Scanner(System.in);
				int n=scn.nextInt();
				int j;
				for(int i=1;i<=n;i++)
				{
					for(j=1;j<=n;j++)
						{System.out.print("*");
						}
					System.out.print("\n");
				}
				}
	}


